package com.example.eventtouch;import android.content.Context;import android.util.AttributeSet;import android.util.Log;import android.view.MotionEvent; import android.widget.Button;public class MyButton extends Button {	private static final String TAG = "MyButton";		public MyButton(Context context, AttributeSet attrs) {		super(context, attrs);	}		@Override	public boolean dispatchTouchEvent(MotionEvent event) {		Log.i("info",TAG + " - dispatchTouchEvent - " + EventUtils.getName(  event.getAction()) );		return super.dispatchTouchEvent(event);	}	@Override	public boolean onTouchEvent(MotionEvent event) {		Log.i("info",TAG + " - onTouchEvent - " + EventUtils.getName(  event.getAction()) );		return super.onTouchEvent(event);	}}