package com.example.eventtouch;import android.content.Context;import android.util.AttributeSet;import android.util.Log;import android.view.MotionEvent;import android.widget.LinearLayout;public class MyLayout extends LinearLayout{	private static final String TAG = "MyLayout";		public MyLayout(Context context, AttributeSet attrs) {		super(context, attrs);	} 	@Override	public boolean dispatchTouchEvent(MotionEvent event) {		Log.i("info",TAG + " - dispatchTouchEvent - " + EventUtils.getName(  event.getAction()) );		return super.dispatchTouchEvent(event);	}	@Override	public boolean onInterceptTouchEvent(MotionEvent event) {		Log.i("info",TAG + " - onInterceptTouchEvent - " + EventUtils.getName(  event.getAction()) );		return super.onInterceptTouchEvent(event);	}		@Override	public boolean onTouchEvent(MotionEvent event) {		Log.i("info",TAG + " - onTouchEvent - " + EventUtils.getName(  event.getAction()) );		return super.onTouchEvent(event);	}}